INSERT INTO `category` (`id`, `name`, `slug`, `is_active`, `created_at`, `updated_at`, `description`, `color`, `icon`) VALUES
	(4, 'Anniversaire', 'anniversaire', 1, '2025-08-17 14:37:06', '2025-08-19 15:25:51', 'Des vannes à sortir juste avant le gâteau. Parfaites pour faire rire toute la table sans plomber les bougies.', 'rgba(254, 252, 1, 0.12)', 'fa-solid fa-cake-candles'),
	(5, 'Saint-Valentin', 'saint-valentin', 1, '2025-08-17 14:55:17', '2025-08-19 15:20:47', 'Traits d’esprit tendres pour briser la glace ou pimenter le tête-à-tête. Romantique, taquin, efficace.', 'rgba(224, 49, 49, 0.12)', 'fa-solid fa-heart'),
	(6, 'Mariage', 'mariage', 1, '2025-08-17 15:16:30', '2025-08-19 15:29:31', 'Blagues bon esprit pour discours, toast ou livre d’or. De quoi faire sourire sans vexer la belle-famille.', 'rgba(132, 94, 247, 0.12)', 'fa-solid fa-ring'),
	(7, 'Bureau', 'bureau', 1, '2025-08-17 15:39:30', '2025-08-19 15:19:50', 'Suffisamment sages pour la machine à café, assez piquants pour décrocher un vrai sourire.', 'rgba(77, 171, 247, 0.12)', 'fa-solid fa-mug-saucer'),
	(8, 'Fête des Mères', 'fete-des-meres', 1, '2025-08-17 16:34:34', '2025-08-19 15:20:05', 'Douces et malines, idéales pour un message attentionné ou un petit toast à maman.', 'rgba(255, 77, 109, 0.12)', 'fa-solid fa-hand-holding-heart'),
	(9, 'Fête des Pères', 'fete-des-peres', 1, '2025-08-17 16:39:42', '2025-08-19 15:27:49', 'Jeux de mots et autodérision “papa-style”, calibrés pour faire rire toute la famille.', 'rgba(56, 217, 169, 0.12)', 'fa-solid fa-user-tie'),
	(10, 'Apéro / Soirée', 'apero-soiree', 1, '2025-08-18 17:05:56', '2025-08-19 15:19:20', 'À dégainer entre deux toasts pour lancer la conversation et chauffer l’ambiance.', 'rgba(255, 168, 36, 0.12)', 'fa-solid fa-champagne-glasses');
