INSERT INTO `joke` (`id`, `category_id`, `title`, `body_text`, `language`, `is_active`, `nsfw`, `created_at`, `updated_at`, `price`, `preview_image`, `view_image`, `description`) VALUES
	(157, 4, 'Bougies soufflées', 'Pourquoi les bougies ne vont jamais à l’école ? Parce qu’elles se font toujours souffler !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Une devinette d’anniversaire qui ne manque pas d’air.'),
	(158, 4, 'Le comble du gâteau', 'Quel est le comble pour un gâteau d’anniversaire ? Se faire couper la parole !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Un comble sucré à raconter le jour J.'),
	(159, 4, 'Fête mortelle', 'Pourquoi les squelettes ne fêtent jamais leur anniversaire ? Parce qu’ils n’ont personne à inviter !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Même les squelettes ont leurs petits soucis d’organisation.'),
	(160, 4, 'Bougie retraitée', 'Que fait une bougie à la retraite ? Elle part en fumée !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Le repos bien mérité d’une bougie de gâteau…'),
	(161, 4, 'Dino blasé', 'Pourquoi les dinosaures n’aiment pas les anniversaires ? Parce qu’ils trouvent ça préhistorique !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Les fêtes d’anniversaire vues par les dinos.'),
	(162, 4, 'Gâteau en retard', 'Que dit un gâteau en retard ? Désolé, j’ai pris un coup de vieux !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Quand un gâteau arrive un peu trop tard…'),
	(163, 4, 'Cap des 40 ans', 'C’est quoi le plus dur après 40 ans ? Se souvenir de son âge !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Une petite réalité qui fait sourire passé la quarantaine.'),
	(164, 4, 'Mot de passe oublié', 'Pourquoi les anniversaires sont comme les mots de passe ? On les oublie toujours au mauvais moment !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Quand le compteur d’âges fait défaut…'),
	(165, 4, 'Juge-bougies', 'Que dit un trentenaire à son gâteau ? Arrête de me juger avec toutes tes bougies !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'La réaction d’un trentenaire devant son gâteau.'),
	(166, 4, 'Bougie susceptible', 'Pourquoi il ne faut jamais dire son âge à une bougie ? Elle risquerait de fondre de honte !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Mieux vaut taire certaines informations à la bougie...'),
	(167, 4, 'Coup de jeune', 'Quelle est la meilleure façon de rajeunir à son anniversaire ? Changer de miroir !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Une astuce de jouvence à tester le jour venu.'),
	(168, 4, 'Piñata discrète', 'Que fait une piñata à l’anniversaire de ses amis ? Elle évite de se faire remarquer…', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'L’instinct de survie d’une certaine invitée surprise.'),
	(169, 4, 'Chaussettes emballées', 'Pourquoi les chaussettes n’aiment pas les anniversaires ? Parce qu’elles finissent toujours dans des paquets !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Une histoire de cadeaux qui ne plaît pas à tout le monde.'),
	(170, 4, 'Guirlandes heureuses', 'Pourquoi les guirlandes sont toujours contentes ? Parce qu’elles sont suspendues au bonheur !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Le secret du sourire permanent des guirlandes.'),
	(171, 4, 'Gâteau sportif', 'Quel est le sport préféré du gâteau au chocolat ? Le sprint… vers l’assiette !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Quand un dessert se met à l’athlétisme humoristique.'),
	(172, 4, 'Stars en confettis', 'Pourquoi les confettis sont les stars de la fête ? Parce qu’ils font toujours sensation !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'De petits éléments qui savent mettre l’ambiance.'),
	(173, 4, 'Cerise sur le gâteau', 'Qu’est-ce qu’un anniversaire sans blague ? Un gâteau sans cerise !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'La petite touche qui fait toute la différence dans une fête.'),
	(174, 4, 'Micro blagueur', 'Pourquoi le micro était content à l’anniversaire ? Parce qu’il avait plein de blagues à raconter !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Un micro très inspiré pendant la fête.'),
	(175, 4, 'Cadeau silencieux', 'Quel est le cadeau le plus silencieux ? Celui qu’on n’ouvre jamais…', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'Une devinette à souffler lors de la distribution des présents.'),
	(176, 4, 'Chapeau fatigué', 'Que dit un chapeau de fête en fin de soirée ? J’en ai plein la tête !', 'fr', 1, 0, '2025-08-17 15:11:36', '2025-08-17 15:11:36', 0.99, 'preview-joker-birthday-preview-449cbdee-0f00-4973-87eb-e55890d21f5f.png', 'view-joker-birthday-view-e8d7ff4a-6c7d-4b4a-a8de-be8bf6303e50.png', 'La confession d’un accessoire de fête après minuit.'),
	(177, 5, 'Cœur introuvable', 'Pourquoi les squelettes détestent la Saint-Valentin ? Parce qu’ils n’ont pas de cœur à offrir !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Un amour impossible du point de vue d’un squelette.'),
	(178, 5, 'Batterie sentimentale', 'Que dit un smartphone à son chargeur le 14 février ? « Sans toi, je ne suis rien. »', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Même les objets high-tech ont leur mot doux le jour J.'),
	(179, 5, 'Cupidon aux fourneaux', 'Pourquoi Cupidon ne devient-il jamais chef cuisinier ? Parce qu’il rate toujours la cuisson… il est trop occupé à viser des cœurs !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Un petit souci de concentration pour le chef ailé.'),
	(180, 5, 'Cadeau de célibataire', 'Qu’est-ce qu’un célibataire offre pour la Saint-Valentin ? Son Wi-Fi gratuit et sa paix intérieure !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Quand on a l’amour de soi, on partage ce qu’on peut.'),
	(181, 5, 'Amour poissonnel', 'Pourquoi les poissons n’aiment pas la Saint-Valentin ? Parce qu’ils trouvent ça un peu trop “poissonnel”…', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Un jeu de mots iodé sur la fête des amoureux.'),
	(182, 5, 'Cupiderman', 'Quel est le super-héros préféré des amoureux ? Cupiderman !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'La fusion inattendue entre Cupidon et un héros bien connu.'),
	(183, 5, 'Rêve d’oreiller', 'Que dit un oreiller à un autre oreiller le 14 février ? « Je rêve de toi chaque nuit. »', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Même les coussins se font des déclarations le 14 février.'),
	(184, 5, 'Fleurs sensibles', 'Pourquoi les fleurs sont-elles de mauvaises dragueuses ? Parce qu’elles se fanent dès qu’on les ignore !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Une petite vérité florale sur la drague.'),
	(185, 5, 'Espoir déçu', 'Quel est le point commun entre la Saint-Valentin et une réunion de famille ? On y va avec de l’espoir, mais on finit souvent déçu !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Une comparaison espiègle entre deux rassemblements.'),
	(186, 5, 'Chocolats condamnés', 'Pourquoi les chocolats n’aiment-ils pas la Saint-Valentin ? Parce qu’ils savent qu’ils vont se faire croquer !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Les états d’âme sucrés des boîtes de chocolats le 14 février.'),
	(187, 5, 'Amour longue distance', 'Comment appelle-t-on deux amoureux qui s’adorent… mais à distance ? Un couple en “mode avion”.', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Une définition moderne pour les couples éloignés.'),
	(188, 5, 'Soirée Sans-Valentin', 'Que fait un célibataire le soir de la Saint-Valentin ? Il fête la “Sans-Valentin” avec une pizza et Netflix en amoureux… avec lui-même.', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'La stratégie d’un célibataire pour passer la soirée du 14 février.'),
	(189, 5, 'Maths romantiques', 'Pourquoi les maths sont-elles romantiques ? Parce qu’elles ont plein de problèmes à résoudre à deux !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Même les équations apprécient le travail en binôme.'),
	(190, 5, 'Calendrier réaliste', 'Que dit un calendrier à la Saint-Valentin ? « T’inquiète, demain c’est fini. »', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Une petite pique temporelle adressée à Cupidon.'),
	(191, 5, 'Amour à crocs', 'Pourquoi les vampires adorent la Saint-Valentin ? Parce que c’est la fête du sang… enfin, de l’amour !', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Quand Dracula s’invite dans les histoires d’amour.'),
	(192, 5, 'Perspective consolante', 'Être célibataire à la Saint-Valentin, c’est mieux qu’être orphelin à la Fête des Mères.', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Une façon grinçante de relativiser sa situation le 14 février.'),
	(193, 5, 'Fleurs posthumes', 'Ma seule chance d’avoir des fleurs à la Saint-Valentin, c’est de me faire enterrer ce jour-là.', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'L’humour noir d’un célibataire en manque de bouquet.'),
	(194, 5, 'Conseil zodiacal', 'S’il vous plaît, le jour de la Saint-Valentin, utilisez des préservatifs. Il y a déjà trop de Scorpions.', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Un message de service public mêlant amour et astrologie.'),
	(195, 5, 'Calendrier idéal', 'La Saint-Valentin devrait tomber le 29 février : comme ça on la fêterait tous les quatre ans.', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Une proposition maligne pour espacer une fête parfois redoutée.'),
	(196, 5, 'Fête ou défaite', 'La Saint-Valentin, c’est la fête des couples… et la défaite des célibataires.', 'fr', 1, 0, '2025-08-17 15:12:08', '2025-08-17 15:12:08', 0.99, 'preview-joker-saint-valentin-preview-3f209d24-09ba-4b9f-b2c6-605833d47d50.png', 'view-joker-saint-valentin-view-4eec2146-dc44-429f-9034-12d999a802e2.png', 'Une réalité amusante pour ceux qui sont seuls en ce jour.'),
	(197, 6, 'Problèmes à deux', 'Le mariage, c’est résoudre à deux des problèmes qu’on n’aurait pas eus tout seul.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'La vision lucide d’un duo marié sur leur quotidien.'),
	(198, 6, 'Bonheur 25 ans', 'Ma femme et moi, on a été heureux pendant 25 ans… puis on s’est rencontrés.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une pointe d’humour sur la vie conjugale avant la rencontre.'),
	(199, 6, 'Belle-mère à avaler', 'Le mariage, ce n’est pas la mer à boire… mais la belle-mère à avaler.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un proverbe révisé pour jeunes mariés.'),
	(200, 6, 'Bigamie punitive', 'Quelle peine risque-t-on pour bigamie ? Deux belles-mères.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une sanction imaginaire qui refroidirait plus d’un polygame.'),
	(201, 6, 'Il vécut heureux', 'Un homme demande à une fille : « Veux-tu m’épouser ? » Elle répond : « Non. » Et cet homme vécut heureux le reste de ses jours.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un conte de fées à l’envers qui fera sourire les invités.'),
	(202, 6, 'Allers-retours frigo-lit', 'Pourquoi les hommes mariés sont-ils plus gros que les célibataires ? Le célibataire ouvre le frigo, ne trouve rien de bon et va se coucher ; l’homme marié ouvre le lit, ne trouve rien de bon et va au frigo.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une explication cocasse sur la prise de poids après le mariage.'),
	(203, 6, 'Retrouver la vue', 'Si l’amour rend aveugle, le mariage rend la vue.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une réflexion mordante sur la clairvoyance après le grand jour.'),
	(204, 6, 'Trois années de mariage', 'Première année de mariage : l’homme parle, la femme écoute. Deuxième année : la femme parle, l’homme écoute. Troisième année : les deux parlent et les voisins écoutent.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'L’évolution sonore d’un couple au fil des années.'),
	(205, 6, 'Choix assumé', 'Ne vous moquez jamais de votre femme : c’est vous qui l’avez choisie.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un rappel ironique pour tout mari un peu trop taquin.'),
	(206, 6, 'Dernier mot', 'La femme a toujours le dernier mot. L’homme qui parle après ne fait que commencer une nouvelle dispute.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une loi non écrite de la communication dans le couple.'),
	(207, 6, 'Bague & corde', 'L’alliance : la bague au doigt, la corde au cou.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une vision un brin sombre du symbole du mariage.'),
	(208, 6, 'Synchronisation ratée', 'Mon mari et moi avons décidé de ne faire l’amour que quand nous en aurions tous les deux envie. Ça fait six mois qu’on n’a plus rien fait.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un pacte conjugal qui ne se passe pas tout à fait comme prévu.'),
	(209, 6, 'Jamais couché fâchés', 'Ils ont juré de ne jamais se coucher fâchés… Résultat, ils n’ont pas dormi depuis six mois.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'La conséquence imprévue d’une règle d’or conjugale.'),
	(210, 6, 'Cause du divorce', 'Le mariage est la cause numéro 1 du divorce.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une statistique imparable sur la vie à deux.'),
	(211, 6, 'La règle des trois', 'L’homme propose, la femme dispose… et la belle-mère impose.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'La répartition des rôles dans un trio familial haut en couleur.'),
	(212, 6, 'Vengeance offerte', 'Quand un homme te vole ta femme, la meilleure vengeance est de le laisser la garder.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un conseil malicieux pour un mari délaissé.'),
	(213, 6, 'Femme ange', 'Ma femme est un ange. – Tu en as de la chance, la mienne est toujours en vie.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un échange à l’humour noir entre maris.'),
	(214, 6, 'Mari archéologue', 'Le meilleur mari qu’une femme puisse avoir, c’est un archéologue : plus elle vieillit, plus il s’intéresse à elle.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un joli paradoxe qui fait sourire mesdames (et messieurs).'),
	(215, 6, 'Motivations trompeuses', 'Les hommes se marient par fatigue, les femmes par curiosité ; les deux sont déçus.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Une citation piquante sur les attentes différentes de chacun.'),
	(216, 6, 'Avant / après mariage', 'Avant le mariage, on ne quitte pas sa fiancée des yeux ; après, on ne peut plus la voir en peinture.', 'fr', 1, 0, '2025-08-17 15:22:27', '2025-08-17 15:22:27', 0.99, 'preview-mariage-preview-f4255d9f-959e-4a4c-a65d-b97e5dfcdce6.png', 'view-mariage-view-4ff0c425-a957-44da-88b5-0d004e0d32e6.png', 'Un contraste drôlement saisissant entre les fiançailles et la vie maritale.'),
	(217, 7, 'Marché équité', 'Ils font semblant de me payer, je fais semblant de travailler.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'La philosophie douteuse d’un employé en fin de mois.'),
	(218, 7, 'Dur et paresseux', 'J’ai travaillé dur pour devenir paresseux.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'L’objectif surprenant d’un bosseur repenti.'),
	(219, 7, 'Arbre des chômeurs', 'Quel est l’arbre préféré des chômeurs ? Le bouleau.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Un jeu de mots pour ceux qui cherchent du boulot.'),
	(220, 7, 'Travail au noir', 'Pourquoi dit-on que les poissons travaillent illégalement ? Parce qu’ils n’ont pas de FISH de paie.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Une preuve que même les poissons fraudent un peu.'),
	(221, 7, 'Pause parfumée', 'Que fait un employé de chez Sephora à sa pause clope ? Il parfume.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Un jeu de mot qui fleure bon la récré chez Sephora.'),
	(222, 7, 'Comptable précieux', 'Que dit-on à l’enterrement d’un comptable ? Qu’il comptait beaucoup pour nous.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Un hommage calculé pour un collègue parti trop tôt.'),
	(223, 7, 'Cartes interdites', 'Pourquoi les fonctionnaires ne jouent plus aux cartes ? Parce que le ministre a interdit de jouer pendant les heures de travail.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Une petite pique sur l’oisiveté au bureau.'),
	(224, 7, 'Bonheur au travail', 'Le bonheur au travail, ça existe : ça s’appelle les vacances.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'La définition la plus honnête du mot « bonheur » au bureau.'),
	(225, 7, 'Travail d\'équipe', 'Le travail d’équipe, c’est important : ça permet de toujours pouvoir rejeter la faute sur quelqu’un d’autre.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'L’un des grands avantages de la collaboration, vu par un malin.'),
	(226, 7, 'Statistique frappante', '3% des accidents du travail sont dus à l’alcool. Donc 97% des accidents sont causés par ceux qui boivent de l’eau.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Une lecture insolite des chiffres de la sécurité au travail.'),
	(227, 7, 'Retard ou avance', 'Quelqu’un qui arrive en retard est un fainéant. Quelqu’un qui part en avance, c’est un pionnier.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Une manière positive de voir les horaires flexibles.'),
	(228, 7, 'Principe de précaution', 'Travailler n’a jamais tué personne, mais pourquoi prendre le risque ?', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Le dicton favori des allergiques au travail.'),
	(229, 7, 'Plan de carrière', 'Je cherche un travail bien payé où je ne fais rien. Mon conseiller m’a dit d’essayer ministre.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Une suggestion de reconversion pleine d’ironie.'),
	(230, 7, 'Réunionite', 'Une réunion, c’est une perte de temps qui se fait à plusieurs.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'La vérité brutale sur certaines séances de travail.'),
	(231, 7, 'Santé préservée', 'Le travail, c’est la santé… alors, pour rester en forme, rien faire c’est la conserver !', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Une approche décomplexée de la prévention en entreprise.'),
	(232, 7, 'Robinson du lundi', 'Le lundi au boulot, je me prends pour Robinson Crusoé : j’attends Vendredi.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Le sentiment hebdomadaire de tous les naufragés du week-end.'),
	(233, 7, 'Travail écologique', 'Je fais un travail écologique : je ne fiche rien, comme ça j’économise mon énergie.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Quand la paresse devient un geste pour la planète.'),
	(234, 7, 'Mode éco', 'Je ne suis pas paresseux, je suis en mode économie d’énergie.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'Une excuse high-tech pour justifier un petit coup de mou.'),
	(235, 7, 'Option sommeil', 'Travailler plus pour gagner plus ? J’ai choisi : dormir plus.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'La devise personnelle d’un employé qui préfère rêver.'),
	(236, 7, 'Zéro erreur', 'Qui ne fait rien ne se trompe jamais… Moi, j’aime ce principe.', 'fr', 1, 0, '2025-08-17 15:39:50', '2025-08-17 15:39:50', 0.99, 'preview-joker-bureau-preview-85af9708-7219-4650-ae9e-3ff10fc38237.png', 'view-joker-bureau-view-6324974e-b9e4-42ba-a5ea-48dcb7c02e88.png', 'La philosophie infaillible de celui qui préfère l’immobilisme.'),
	(237, 8, 'Rien de perdu', 'Rien n’est vraiment perdu jusqu’à ce que maman ne puisse pas le trouver.', 'fr', 1, 0, '2025-08-17 16:34:51', '2025-08-17 16:34:51', 0.99, 'preview-fete-des-mere-preview-ceaa2ce5-341d-45b9-aa8a-e6b356f33179.png', 'view-fete-des-mere-view-f4b3f5ee-3da3-4a12-a894-00a8b37df595.png', 'Le flair infaillible d’une maman pour retrouver n’importe quoi.'),
